import emailjs from '@emailjs/browser';

// ...inside your submit handler:

const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
  e.preventDefault();

  emailjs
    .sendForm(
      'service_jrasc2b',          // your service ID
      'YOUR_TEMPLATE_ID',         // template from EmailJS
      e.currentTarget,            // the form element
      {
        publicKey: 'AcSa1aFlL36Sxc6ge',   // <-- your public key
      }
    )
    .then(
      () => {
        console.log('SUCCESS');
        // show success message in UI
      },
      (error) => {
        console.error('FAILED', error);
        // show error message in UI
      }
    );
};

